'  FUNCTION ai_agent_execute( agent_endpoint_ocid varchar2, agent_session_id varchar2, message varchar2, filter_folder varchar2 default '''' ) return CLOB; ',
'  FUNCTION create_pre_authenticated_request( url varchar2, page number) return varchar2; ',
'  FUNCTION agent2html( res CLOB, session_id varchar2 ) return CLOB;',
'end "AI_AGENT";',
'/',
'',
'',
'',
'',
'create or replace package body "AI_AGENT" as',
'',
'g_region varchar2(256) := '''';',
'g_credential_name varchar2(256) := '''';',
'',
'-------------------------------------------------------------------------------',
'',
'PROCEDURE LOG( a_context varchar2, a_clob CLOB ) is',
'   PRAGMA AUTONOMOUS_TRANSACTION;',
'BEGIN',
'   INSERT INTO DOCS_LOGS( LOG_DATE, LOG_CONTEXT, LOG_TEXT ) VALUES( sysdate, a_context, a_clob );',
'   -- apex_debug.enable(p_level => apex_debug.c_log_level_info); ',
'   -- apex_debug.info( p_message => a_clob );',
'   COMMIT;',
'END;',
'',
'-------------------------------------------------------------------------------',
'',
'FUNCTION ai_agent_session( agent_endpoint_ocid varchar2) return CLOB is ',
'    resp DBMS_CLOUD_TYPES.resp; ',
'    json_response CLOB; ',
'    s VARCHAR2(32767); ',
'    je JSON_ELEMENT_T;',
'    jo JSON_OBJECT_T;',
'    res clob;',
'begin',
'  je := JSON_ELEMENT_T.parse(''{',
'    "idleTimeoutInSeconds": 3600',
'}'');  ',
'  jo := treat(je AS JSON_OBJECT_T);',
'  log( ''<ai_agent_session> request'', jo.to_clob());',
'  log( ''<ai_agent_session> LENGTH'', ''LENGTH='' || LENGTH(jo.to_clob()));',
'  resp := DBMS_CLOUD.send_request( ',
'        credential_name => ''OCI$RESOURCE_PRINCIPAL'', ',
'        uri =>''https://agent-runtime.generativeai.'' || g_region || ''.oci.oraclecloud.com/20240531/agentEndpoints/''||agent_endpoint_ocid||''/sessions'',',
'        method => ''POST'', ',
'        body => APEX_UTIL.CLOB_TO_BLOB(jo.to_clob)                                                                                         ',
'    ); ',
'  json_response := DBMS_CLOUD.get_response_text(resp); ',
'  log( ''<ai_agent_session> response'', json_response);',
'  res := JSON_VALUE(json_response, ''$.id'');',
'  log( ''<ai_agent_session> res'', res);  ',
'  return res; ',
'end;',
'',
'-------------------------------------------------------------------------------',
'',
'FUNCTION ai_agent_execute( agent_endpoint_ocid varchar2, agent_session_id varchar2, message varchar2, filter_folder varchar2 ) return CLOB is ',
'    resp DBMS_CLOUD_TYPES.resp; ',
'    json_response CLOB; ',
'    s VARCHAR2(32767); ',
'    je JSON_ELEMENT_T;',
'    jo JSON_OBJECT_T;',
'    joToolParam JSON_OBJECT_T;',
'    res clob;',
'begin',
'  je := JSON_ELEMENT_T.parse(''{',
'    "userMessage": "Your question",',
'    "sessionId": "xxxx",',
'    "shouldStream": false',
'  }'');  ',
'  jo := treat(je AS JSON_OBJECT_T);',
'  jo.put(''userMessage'', message);',
'  jo.put(''sessionId'', agent_session_id);',
'',
'  -- RAG filter',
'  --   "toolParameters": {',
'  --     "rag": "{"filterConditions\":[{\"field\":\"type\",\"field_type\":\"string\",\"operation\":\"contains\",\"value\":\"Music\"}]}"',
'  --   }',
'  log( ''filter_folder'', filter_folder ); ',
'  if filter_folder='''' then',
'      log( ''no filter'', ''-'' );     ',
'  elsif filter_folder is not null then',
'    declare',
'      je2 JSON_ELEMENT_T;',
'      jo2 JSON_OBJECT_T;',
'      aFilterConditions JSON_ARRAY_T;',
'      eFilterCondition JSON_ELEMENT_T;',
'      oFilterCondition JSON_OBJECT_T;         ',
'      jo3 JSON_OBJECT_T;',
'      rag_filter varchar2(1024);',
'    begin',
'      je2 := JSON_ELEMENT_T.parse(''{"filterConditions":[{"field":"folder","field_type":"string","operation":"contains","value":"xxxx"}]}'');',
'      jo2 := treat(je2 AS JSON_OBJECT_T);',
'      log( ''jo2'', jo2.to_string );  ',
'      aFilterConditions := jo2.get_Array(''filterConditions'');  ',
'      log( ''aFilterConditions'', aFilterConditions.to_string );    ',
'      eFilterCondition := aFilterConditions.get(0);    ',
'      oFilterCondition := TREAT( eFilterCondition AS JSON_OBJECT_T);',
'      oFilterCondition.put(''value'', filter_folder);     ',
'      rag_filter := je2.to_string;',
'      log( ''rag_filter'', rag_filter );    ',
'      jo3 := json_object_t.parse (''{"rag":"xxx"}'');  ',
'      jo3.put( ''rag'', rag_filter );',
'      jo.put( ''toolParameters'', jo3);',
'    end;',
'  end if;',
'',
'  log( ''<ai_agent_execute> request'', jo.to_clob());',
'  log( ''<ai_agent_execute> LENGTH'', ''LENGTH='' || LENGTH(jo.to_clob()));',
'  resp := DBMS_CLOUD.send_request( ',
'        credential_name => ''OCI$RESOURCE_PRINCIPAL'', ',
'        uri =>''https://agent-runtime.generativeai.'' || g_region || ''.oci.oraclecloud.com/20240531/agentEndpoints/''||agent_endpoint_ocid||''/actions/chat'',',
'        method => ''POST'', ',
'        body => APEX_UTIL.CLOB_TO_BLOB(jo.to_clob)                                                                                         ',
'    ); ',
'  /*',
'  resp := DBMS_CLOUD.send_request( ',
'        credential_name => ''OCI$RESOURCE_PRINCIPAL'', ',
'        uri =>''https://agent-runtime.generativeai.'' || g_region || ''.oci.oraclecloud.com/20240531/agentEndpoints/''||agent_endpoint_ocid||''/sessions/''||agent_session_id||''/actions/execute'',',
