'-------------------------------------------------------------------------------',
'',
'FUNCTION create_pre_authenticated_request( url varchar2, page number ) return varchar2 is',
'  resp DBMS_CLOUD_TYPES.resp;   ',
'  json_response CLOB; ',
'  s VARCHAR2(32767); ',
'  je JSON_ELEMENT_T;',
'  jo JSON_OBJECT_T;  ',
'',
'  res varchar2(4096);',
