'  url2 := substr( url, pos_sep+3 );',
'  dbms_output.put_line( url1 );',
'  dbms_output.put_line( url2 );',
'',
'  je := JSON_ELEMENT_T.parse(''',
'  { "accessType":"ObjectRead",',
'    "name":"par-object-20241118-1126",',
'    "timeExpires":"2024-11-25T10:26:34.017Z",',
'    "objectName":"xxxx.pdf"',
'  }'');  ',
'  d := to_char( systimestamp + 10.0/1440.0, ''YYYY-MM-DD"T"HH24:MI:SS"Z"'');',
'  jo := treat(je AS JSON_OBJECT_T);',
'  jo.put(''name'', ''apex'' || d);',
'  jo.put(''timeExpires'', d);',
'  jo.put(''objectName'', utl_url.unescape( url2, url_charset => ''UTF-8'' ));',
'  log( ''<create_pre_authenticated_request> request'', jo.to_clob());',
'  log( ''<create_pre_authenticated_request> LENGTH'', ''LENGTH='' || LENGTH(jo.to_clob()));',
'  resp := DBMS_CLOUD.send_request( ',
