'',
'-------------------------------------------------------------------------------',
'',
'FUNCTION json2html( input CLOB ) return CLOB is',
'    v_json_array   JSON_ARRAY_T;',
'    v_json_obj     JSON_OBJECT_T;',
'',
'    v_html_output  CLOB := ''<table class="sql">'';',
'    v_keys         DBMS_SQL.VARCHAR2_TABLE;',
'    v_key_index    PLS_INTEGER := 1;',
'    v_temp_key     VARCHAR2(4000);',
'    v_value        VARCHAR2(4000);',
'',
'    v_key_set      PLS_INTEGER := 0;',
'BEGIN',
'    log(''<json2html>input='', input); ',
'',
'    -- Parse the JSON input into a JSON_ARRAY_T',
'    v_json_array := JSON_ARRAY_T.parse(input);',
'',
'    -- Step 1: Collect all unique keys',
'    FOR i IN 0 .. v_json_array.get_size - 1 LOOP',
'        v_json_obj := TREAT(v_json_array.get(i) AS JSON_OBJECT_T);',
'        IF v_json_obj IS NOT NULL THEN',
'            FOR j IN 1 .. v_json_obj.get_keys().COUNT LOOP',
'                v_temp_key := v_json_obj.get_keys()(j);',
'                -- Check for duplicates before adding',
'                v_key_set := 0;',
'                FOR k IN 1 .. v_key_index - 1 LOOP',
'                    IF v_keys(k) = v_temp_key THEN',
'                        v_key_set := 1;',
'                        EXIT;',
'                    END IF;',
'                END LOOP;',
'',
'                IF v_key_set = 0 THEN',
'                    v_keys(v_key_index) := v_temp_key;',
'                    v_key_index := v_key_index + 1;',
'                END IF;',
'            END LOOP;',
'        END IF;',
'    END LOOP;',
'',
'    -- Step 2: Build table header',
'    v_html_output := v_html_output || ''<tr>'';',
'    FOR i IN 1 .. v_key_index - 1 LOOP',
'        v_html_output := v_html_output || ''<th>'' || v_keys(i) || ''</th>'';',
'    END LOOP;',
'    v_html_output := v_html_output || ''</tr>'';',
'',
'    -- Step 3: Build rows',
'    FOR i IN 0 .. v_json_array.get_size - 1 LOOP',
'        v_json_obj := TREAT(v_json_array.get(i) AS JSON_OBJECT_T);',
'        v_html_output := v_html_output || ''<tr>'';',
'',
'        FOR j IN 1 .. v_key_index - 1 LOOP',
'            BEGIN',
'                v_value := v_json_obj.get_string(v_keys(j));',
'            EXCEPTION',
'                WHEN OTHERS THEN',
'                    v_value := ''''; -- In case the key is missing',
'            END;',
'',
'            v_html_output := v_html_output || ''<td>'' || NVL(v_value, '''') || ''</td>'';',
'        END LOOP;',
'',
'        v_html_output := v_html_output || ''</tr>'';',
'    END LOOP;',
'',
'    -- Step 4: Close table',
'    v_html_output := v_html_output || ''</table>'';',
'',
'    -- Result',
'    log(''</json2html>'', v_html_output);     ',
'    return v_html_output;',
'END;',
'',
'-------------------------------------------------------------------------------',
'',
'FUNCTION ADD_TOOL( arguments varchar2 ) return varchar2 is',
'  number1 number;',
'  number2 number;',
'begin ',
'  number1 := TO_NUMBER(JSON_VALUE(arguments, ''$.number1''));',
'  number2 := TO_NUMBER(JSON_VALUE(arguments, ''$.number2''));',
'  return ''ADD: '' || to_char(number1 + number2);',
'exception',
'  when others then',
'    return ''ERROR: add tools. Failed to add the numbers. Arguments='' || arguments;',
'end;',
'',
'-------------------------------------------------------------------------------',
'',
'FUNCTION agent2html( res CLOB, session_id varchar2 ) return CLOB is',
'  message clob;',
'  citation clob := '''';',
'  url clob;',
'  url_page clob;',
'  doc_name varchar2(4096);',
'  je JSON_ELEMENT_T;',
'  jo JSON_OBJECT_T;',
'  ja JSON_ARRAY_T;  ',
'  a_page JSON_ARRAY_T;  ',
'  l_id number;',
'  doc_page number := null;',
'  arguments varchar2(4096);',
'  arg_message varchar2(4096);',
'  tool_name varchar2(4096);',
'begin',
'  je := JSON_ELEMENT_T.parse(res);',
'  jo := treat(je AS JSON_OBJECT_T);',
'  citation := ''<div class="citation"> + <span class="hide">'';',
'  if jo.get_Object(''message'') is not null then',
'    message := JSON_VALUE(res, ''$.message.content.text''); ',
'    ai_agent.log(''message'', message); ',
'    if dbms_lob.INSTR( message, ''{"generatedQuery"'' )=1 then',
'      -- SQL TOOL',
'      citation := citation || ''SQL: <ul><li>generatedQuery: '' || JSON_VALUE(message, ''$.generatedQuery'') || ''</li>''; ',
'      message := json_object_t.parse(message).get(''executionResult'').to_string;',
'      message := ai_agent.json2html( message );',
'    else  ',
'      -- RAG TOOL',
'      message := APEX_MARKDOWN.TO_HTML( message );',
'      -- Get the Citations',
'      je := jo.get_Object(''message'').get_Object(''content'').get_Array(''citations'');',
'      ja := TREAT (je AS JSON_ARRAY_T);',
'      if ja is null or ja.get_size = 0 then ',
'         citation := '''';',
'      else',
'        citation := citation || ''Citations: <ul>'';',
'        FOR indx IN 0 .. ja.get_size - 1',
'        LOOP',
'          doc_page := null;',
'          je := ja.get(indx);',
'          jo := treat(je AS JSON_OBJECT_T);',
'          url := jo.get_Object(''sourceLocation'').get_string(''url'');',
'          if url like ''https://objectstorage.%'' then',
'            doc_name := SUBSTR(url,INSTR(url, ''/'', -1)+1);',
'          else',
'            doc_name := url;',
'            url_page := url;',
'          end if;',
'          je := jo.get_Array(''pageNumbers'');',
'          a_page := TREAT (je AS JSON_ARRAY_T);',
'          if a_page is not null and a_page.get_size > 0 then',
'            doc_name := doc_name || '' ('';',
'            FOR i_page IN 0 .. a_page.get_size - 1 loop',
'              if i_page>0 then',
'                doc_name := doc_name || '','';',
'              else',
'                doc_page := a_page.get_number(i_page);',
'              end if;',
'              doc_name := doc_name || a_page.get_number(i_page);',
'            end loop;',
'            doc_name := doc_name || '')'';',
'          end if; ',
'          if url like ''https://objectstorage.%'' then',
'            insert into ai_agent_rag_citation( session_id, url, page, snippet) values (session_id, url, doc_page, ''-'') RETURNING id INTO l_id;',
'            url_page := APEX_PAGE.GET_URL ( p_page => 2, p_items  => ''P2_CITATION_ID'', p_values => l_id );',
'          end if;  ',
'          url_page := ''<a href="'' || url_page || ''" target="_blank">'' || doc_name || ''</a>'';',
'          citation := citation || ''<li>'' || url_page || '' - '' || replace(jo.get_string(''sourceText''), ''"'', '''''''') || ''</li>'';',
'        END LOOP;',
'      END IF;',
'    end if;',
'  else',
'    -- CLIENT TOOL',
'    tool_name :=  JSON_VALUE(res, ''$.requiredActions[0].functionCall.name'');',
'    arguments := JSON_VALUE(res, ''$.requiredActions[0].functionCall.arguments'');',
'    if tool_name = ''add'' then',
'      message := ADD_TOOL( arguments );',
'    else ',
'      message := ''ERROR: tool not implemented: ''|| tool_name;',
'    end if;',
'    message := ''<div>'' || message || ''</div>'';    ',
'    citation := citation || ''TOOL <ul><li>action: '' || JSON_VALUE(res, ''$.requiredActions[0].requiredActionType'') || ''</li>'';',
'    citation := citation || ''<li>name: '' || tool_name || ''</li>'';',
'    citation := citation || ''<li>arguments: '' || arguments || ''</li>'';',
'  end if;',
'  if citation!='''' then',
'    citation := citation || ''</ul></span></div>''; ',
'  end if;',
'  return  message || citation;',
'end;',
'',
'begin',
